// src/index.ts
import debug2 from "debug";
const defaultOptions = {};
function ErrorOverlay(options = {}) {
  const resolvedOptions = Object.assign({}, defaultOptions, options);
  debug2("vite-plugin-error-overlay:config")(resolvedOptions);
  process.on("uncaughtExceptionMonitor", (err) => {
    debug2("vite-plugin-error-overlay:error")(err);
  });
  return {};
}
export {
  ErrorOverlay
};
